      {401, OpenFGA.Model.UnauthenticatedResponse},
      {403, OpenFGA.Model.ForbiddenResponse},
      {404, OpenFGA.Model.PathUnknownErrorMessageResponse},
      {409, OpenFGA.Model.AbortedMessageResponse},
      {422, OpenFGA.Model.UnprocessableContentMessageResponse},
      {500, OpenFGA.Model.InternalErrorMessageResponse}
    ])
  end
end
